program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=com" CXX=xlC CXXFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=com" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CXX=aCC CXXFLAGS="-AA -z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CXX=aCC CXXFLAGS="+O2 -AA -z +Ofltacc +Olit=all +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CXX=aCC CXXFLAGS="-AA -z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CXX=CC CXXFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -ptused -FE:template_in_elf_section -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; x86_64-redhat-linux*)
CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -msym -readonly_strings" CXX=cxx CXXFLAGS="-O2 -model ansi -ieee -msym -readonly_strings \ -timplicit_local" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CXX=CC CXXFLAGS="-xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CXX=CC CXXFLAGS="-xO2 -xtarget=ultra2 -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CXX=CC CXXFLAGS="-xO2 -xtarget=ultra2 -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CXX="${CXX:-g++}" CXXFLAGS="${CXXFLAGS:--O2}" \ LDFLAGS="${SB_VAR_GCC_RT+${CC_LD_RT}${SB_VAR_GCC_RT_LIB}} \ ${LDFLAGS+${LDFLAGS}}" --without-pike --without-chicken \ --without-php4 ${SB_VAR_OCAML+--with-ocaml} \ ${SB_VAR_TCL+--with-tclconfig="${SB_VAR_TCL}/lib"} \ --without-mzscheme --without-csharp --without-java \ --disable-dependency-tracking --without-maximum-compile-warnings \ --with-swiglibdir=${_datadir} ${ARGS+${ARGS}} \ --mandir="${SB_INSTALL_PREFIX}/man" --prefix=${SB_INSTALL_PREFIX}
