module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CPP="cc -E" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" CPP="${CPP+${CPP}}" \ CPPFLAGS="${SB_VAR_NCURSES+-I${SB_VAR_NCURSES_INC}} \ ${SB_VAR_GETTEXT+-I${SB_VAR_GETTEXT_INC}} \ ${SB_VAR_LIBICONV+-I${SB_VAR_LIBICONV_INC}}" \ LDFLAGS="${SB_VAR_NCURSES+-L${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_GETTEXT+-L${SB_VAR_GETTEXT_LIB}} \ ${SB_VAR_LIBICONV+-L${SB_VAR_LIBICONV_LIB}} \ ${SB_VAR_NCURSES+${CC_LD_RT}${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_GETTEXT+${CC_LD_RT}${SB_VAR_GETTEXT_LIB}} \ ${SB_VAR_LIBICONV+${CC_LD_RT}${SB_VAR_LIBICONV_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ ${SHELL} ./configure --without-x --enable-gui=no \ --with-tlib=ncurses --enable-cscope --enable-xim \ --enable-multibyte --with-features=huge \ --disable-perlinterp --disable-pythoninterp \ --disable-tclinterp --disable-rubyinterp \ --with-global-runtime=${_datadir} --datadir=${_datadir} \ --sysconfdir=${_sysconfdir} --mandir="${SB_INSTALL_PREFIX}/man" \ --with-compiledby="The Written Word, Inc." \ --with-modified-by="The Written Word, Inc." \ --prefix=${SB_INSTALL_PREFIX}
cat >>src/feature.h <<_EOT_ #define SYS_VIMRC_FILE "${_sysconfdir}/vimrc" #define SYS_GVIMRC_FILE "${_sysconfdir}/gvimrc" _EOT_

case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CPP="cc -E" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" CPP="${CPP+${CPP}}" \ CPPFLAGS="${SB_VAR_XPM+-I${SB_VAR_XPM_INC}} \ ${SB_VAR_NCURSES+-I${SB_VAR_NCURSES_INC}} \ ${SB_VAR_GETTEXT+-I${SB_VAR_GETTEXT_INC}} \ ${SB_VAR_LIBICONV+-I${SB_VAR_LIBICONV_INC}} \ ${SB_VAR_LIBPNG+-I${SB_VAR_LIBPNG_INC}}" \ LDFLAGS="${SB_VAR_FCPACKAGE+-L${SB_VAR_FCPACKAGE_LIB}} \ ${SB_VAR_XPM+-L${SB_VAR_XPM_LIB}} \ ${SB_VAR_NCURSES+-L${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_GETTEXT+-L${SB_VAR_GETTEXT_LIB}} \ ${SB_VAR_LIBICONV+-L${SB_VAR_LIBICONV_LIB}} \ ${SB_VAR_LIBPNG+-L${SB_VAR_LIBPNG_LIB}} \ ${SB_VAR_XPM+${CC_LD_RT}${SB_VAR_XPM_LIB}} \ ${SB_VAR_NCURSES+${CC_LD_RT}${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_GETTEXT+${CC_LD_RT}${SB_VAR_GETTEXT_LIB}} \ ${SB_VAR_LIBICONV+${CC_LD_RT}${SB_VAR_LIBICONV_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="${SB_VAR_LIBGLIB+${SB_VAR_LIBGLIB_LIB}/pkgconfig}\ ${SB_VAR_LIBGDKPIXBUF+:${SB_VAR_LIBGDKPIXBUF_LIB}/pkgconfig}\ ${SB_VAR_LIBGTK+:${SB_VAR_LIBGTK_LIB}/pkgconfig}\ ${SB_VAR_LIBCAIRO+:${SB_VAR_LIBCAIRO_LIB}/pkgconfig}\ ${SB_VAR_LIBPANGO+:${SB_VAR_LIBPANGO_LIB}/pkgconfig}\ ${SB_VAR_LIBATK+:${SB_VAR_LIBATK_LIB}/pkgconfig}\ ${SB_VAR_FCPACKAGE+:${SB_VAR_FCPACKAGE_LIB}/pkgconfig}\ ${SB_VAR_LIBTTF+:${SB_VAR_LIBTTF_LIB}/pkgconfig}\ ${SB_VAR_GETTEXT+:${SB_VAR_GETTEXT_LIB}/pkgconfig}\ ${SB_VAR_LIBPNG+:${SB_VAR_LIBPNG_LIB}/pkgconfig}\ ${SB_VAR_LIBZ+:${SB_VAR_LIBZ_LIB}/pkgconfig}" \ ${SHELL} ./configure --enable-gui=${_gui-gtk2} --with-tlib=ncurses \ --enable-cscope --enable-xim --enable-multibyte \ --with-features=huge --disable-perlinterp \ --disable-pythoninterp --disable-tclinterp --disable-rubyinterp \ --with-global-runtime=${_datadir} --datadir=${_datadir} \ --sysconfdir=${_sysconfdir} --mandir="${SB_INSTALL_PREFIX}/man" \ --with-compiledby="The Written Word, Inc." \ --with-modified-by="The Written Word, Inc." \ --prefix=${SB_INSTALL_PREFIX}
cat >>src/feature.h <<_EOT_ #define SYS_VIMRC_FILE "${_sysconfdir}/vimrc" #define SYS_GVIMRC_FILE "${_sysconfdir}/gvimrc" _EOT_
