program.configure:
case "${SB_SYSTYPE}" in ia64*-hpux*) ${SB_PATH_SED} -i -e "\ s!^XCOMM LOCAL_LDFLAGS.*!LOCAL_LDFLAGS = -Wl,+nodefaultrpath!" \ Imakefile.in ;; esac
${SHELL} ./configure --with-app-defaults-dir=${_datadir}/app-defaults \ --libexecdir="${SB_INSTALL_PREFIX}/bin" --prefix=${SB_INSTALL_PREFIX}
xmkmf
