module.configure:
case "${SB_SYSTYPE}" in *-solaris2.[67]) CPPFLAGS="-DFUNCPROTO"
ARGS="--disable-xsync" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ LDFLAGS="${LDFLAGS+${LDFLAGS}}" \ CPPFLAGS="${CPPFLAGS+${CPPFLAGS}}" \ PKG_CONFIG_PATH="\ ${SB_VAR_LIBGDKPIXBUF_LIB}/pkgconfig:\ ${SB_VAR_LIBPNG_LIB}/pkgconfig:\ ${SB_VAR_LIBZ_LIB}/pkgconfig:${SB_VAR_FCPACKAGE_LIB}/pkgconfig:\ ${SB_VAR_LIBGLIB_LIB}/pkgconfig:${SB_VAR_LIBGTK_LIB}/pkgconfig:\ ${SB_VAR_LIBPANGO_LIB}/pkgconfig:${SB_VAR_LIBCAIRO_LIB}/pkgconfig:\ ${SB_VAR_LIBATK_LIB}/pkgconfig:${SB_VAR_LIBTTF_LIB}/pkgconfig:\ ${SB_VAR_GETTEXT_LIB}/pkgconfig:${SB_VAR_LIBXML_LIB}/pkgconfig:\ ${SB_VAR_LIBICONV+${SB_VAR_LIBICONV_LIB}/pkgconfig:}\ ${SB_VAR_GNOME_LIB}/pkgconfig:\ ${SB_VAR_LIBGLADE_LIB}/pkgconfig:\ ${SB_VAR_DBUS_LIB}/pkgconfig:\ ${SB_INSTALL_PREFIX}/lib/pkgconfig" \ ${ARGS+${ARGS}} --disable-dependency-tracking \ --mandir="${SB_INSTALL_PREFIX}/man" --datadir=${_datadir} \ --datarootdir=${_datadir} --sysconfdir=${_sysconfdir} \ --prefix=${SB_INSTALL_PREFIX}
