# create Docbook XML catalog if it doesn't exist if [ ! -f ${_docbook_xml_catalog} ]; then ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --create \ ${_docbook_xml_catalog} fi
# create entry in super catalog to Docbook XML catalog entries ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "delegatePublic" "-//OASIS//DTD DocBook XML" \ "file://${_docbook_xml_catalog}" ${_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "delegatePublic" "-//OASIS//DTD DocBook XML" \ "file://${_docbook_xml_catalog}" ${_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "delegateSystem" "http://www.oasis-open.org/docbook/" \ "file://${_docbook_xml_catalog}" ${_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "delegateURI" "http://www.oasis-open.org/docbook/" \ "file://${_docbook_xml_catalog}" ${_xml_catalog}
for _file in calstblx.dtd catalog.xml dbcentx.mod dbgenent.mod \ dbhierx.mod dbnotnx.mod dbpoolx.mod docbookx.dtd htmltblx.mod \ soextblx.dtd; do ${SB_PATH_INSTALL} -m 644 -o root -g 0 ${_file} ${_dtd_datadir} done for _file in ent/*; do ${SB_PATH_INSTALL} -m 644 -o root -g 0 ${_file} ${_dtd_datadir}/ent done
# install XML catalog entries ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" "-//OASIS//DTD DocBook XML CALS Table Model V4.5//EN" \ "xml-dtd-4.5/calstblx.dtd" ${_docbook_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" "-//OASIS//DTD DocBook XML V4.5//EN" \ "xml-dtd-4.5/docbookx.dtd" ${_docbook_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" "-//OASIS//DTD XML Exchange Table Model 19990315//EN" \ "xml-dtd-4.5/soextblx.dtd" ${_docbook_xml_catalog}
${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" "-//OASIS//ENTITIES DocBook XML Character Entities V4.5//EN" \ "xml-dtd-4.5/dbcentx.mod" ${_docbook_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" \ "-//OASIS//ENTITIES DocBook XML Additional General Entities V4.5//EN" \ "xml-dtd-4.5/dbgenent.mod" ${_docbook_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" "-//OASIS//ELEMENTS DocBook XML Document Hierarchy V4.5//EN" \ "xml-dtd-4.5/dbhierx.mod" ${_docbook_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" "-//OASIS//ENTITIES DocBook XML Notations V4.5//EN" \ "xml-dtd-4.5/dbnotnx.mod" ${_docbook_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "public" "-//OASIS//ELEMENTS DocBook XML Information Pool V4.5//EN" \ "xml-dtd-4.5/dbpoolx.mod" ${_docbook_xml_catalog}
${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "rewriteSystem" "http://www.oasis-open.org/docbook/xml/4.5" \ "xml-dtd-4.5" ${_docbook_xml_catalog} ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --add \ "rewriteURI" "http://www.oasis-open.org/docbook/xml/4.5" \ "xml-dtd-4.5" ${_docbook_xml_catalog}

module.uninstall:
_dtd_datadir="${_datadir}/docbook/xml-dtd-4.5"

# remove XML catalog entries
for _file in calstblx.dtd docbookx.dtd soextblx.dtd dbcentx.mod \
dbhierx.mod dbnotnx.mod dbpoolx.mod; do
  ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --del \
    "xml-dtd-4.5/${_file}" ${_docbook_xml_catalog}
done

${SB_VAR_LIBXML}/bin/xmlcatalog --noout --del \
  "xml-dtd-4.5" ${_docbook_xml_catalog}

# remove super catalog entries
${SB_PATH_SB} -q module-names -i ${SB_PROG_NAME}-${SB_PROG_VER} |\
${SB_PATH_EGREP} -v ${SB_MODULE_NAME} |\
${SB_PATH_EGREP} docbook-xml >/dev/null || {
  ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --del \
    "file://${_docbook_xml_catalog}" ${_xml_catalog}
}

rm -rf ${_dtd_datadir}

rmdir ${_datadir}/docbook 2>/dev/null
rmdir ${_datadir} 2>/dev/null

:
